    [Parameter]
    public int n { get; set; }
    List<Role> roles_A;
    List<Role> roles_B;
    List<Role> boat;
    Position pos = Position.sideA;
    string drive = "align-self-start";
    string replay = "d-none";
    bool fail = false;
    bool success = false;
    bool no_more = false;

    bool answer = false;
    public enum Position
    {
        sideA,
        sideB
    }
    protected override void OnInitialized()
    {
        string img_K = "img/Kai.png";
        string img_G = "img/Gong.png";
        roles_A = new List<Role>();
        // Kai
        for (int i = 0; i < n; i++)
        {
            roles_A.Add(new Role() { id = i, role = "K", image = img_K, visible = true });
        }

        // Gong
        int tmp = (n == 6) ? 5 : n;
        for (int i = 0; i < tmp; i++)
        {
            roles_A.Add(new Role() { id = i, role = "G", image = img_G, visible = true });

        }

        // Boat
        boat = new List<Role>();

        // Other side
        roles_B = new List<Role>();
    }
    public void A_ON(Role r)
    {
        if (pos == Position.sideB)
        {
            return;
        }
        if (boat.Count == (n > 3 ? 3 : 2))
        {
            return;
        }
        if (roles_A.Remove(r))
        {
            boat.Add(r);
        }
    }

    public void B_ON(Role r)
    {
        if (pos == Position.sideA)
        {
            return;
        }
        if (boat.Count == (n > 3 ? 3 : 2))
        {
            return;
        }
        if (roles_B.Remove(r))
        {
            boat.Add(r);
        }
    }

    public void Cancel(Role r)
    {
        if (boat.Remove(r))
        {
            if (pos == Position.sideA)
            {
                roles_A.Add(r);
            }
            else
            {
                roles_B.Add(r);
            }
        }
    }

    public void Drive()
    {
        if (boat.Count == 0)
        {
            return;
        }

        if (pos == Position.sideA)
        {
            foreach (var r in boat)
            {
                roles_B.Add(r);
            }
            pos = Position.sideB;
        }
        else
        {
            foreach (var r in boat)
            {
                roles_A.Add(r);
            }
            pos = Position.sideA;
        }

        boat.Clear();
        drive = pos == Position.sideA ? "align-self-start" : "align-self-end";
        if (isFail())
        {
            fail = true;
            drive = "d-none";
            replay = "align-self-center";
        }
        if (roles_A.Count == 0)
        {
            success = true;
            drive = "d-none";
            replay = "align-self-center";
            if (n == 6)
            {
                no_more = true;
            }
        }
    }

    private bool isFail()
    {
        // Side A
        if (roles_A.Where((x) => x.role == "K").Count() < roles_A.Where((x) => x.role == "G").Count())
        {
            if (roles_A.Where((x) => x.role == "K").Count() != 0)
            {
                return true;
            }
        }
        // Side B
        if (roles_B.Where((x) => x.role == "K").Count() < roles_B.Where((x) => x.role == "G").Count())
        {
            if (roles_B.Where((x) => x.role == "K").Count() != 0)
            {
                return true;
            }
        }
        return false;
    }

    public void Replay()
    {
        OnInitialized();

        pos = Position.sideA;
        drive = "align-self-start";
        replay = "d-none";
        fail = false;
        success = false;
        no_more = false;

        answer = false;
    }

    public void Next()
    {
        if (n < 6)
        {
            n++;
            Replay();
        }
        else
        {

        }
    }

